import React, { Component } from "react";
import ScrumTeamItem from './ScrumTeamItem';
import SeoTitle from '../Title/SeoTitle';
import { translate } from "react-translate";


class ScrumTeam extends Component {
    
    render(){

        let {Â t } = this.props;

        return(
        <section className="scrum_team_area bg-white">
            <div className="container">
                <div className="hosting_title security_title text-center">
                  <SeoTitle 
                  Tclass="text-center" 
                  Title={t(this.props.Title)}
                  TitleP={t(this.props.Descrip)} />
                </div>
                <div className="row scrum_team_inner pt-5">
                    <ScrumTeamItem image="chat.png"  title="Product owner" 
                    ptext="In charge of maximizing value and managing the Product Backlog."
                    ptext2="He is the person who knows and understands what the users' needs are, knows the business from head to toe, and is responsible for transmitting that understanding to the development team (or solution team)." 
                    />
                    <ScrumTeamItem image="leader.png"  title="Development team" 
                    ptext="It is made up of developers, who executed the complex work beyond the code. They carry out project activities to create and deliver a preview of the finished product in each sprint." 
                    />
                    <ScrumTeamItem image="meetup.png"  title="Scrum Master" 
                    ptext="He is a leader who is at the service of the Scrum Team and the organization, helping them understand and live agility. He Know the status of the project, take care of events and that the team is multifunctional"
                    /> 
                    
                </div>
            </div>
        </section>
        )
    }
}

export default translate('ScrumTeam')(ScrumTeam);
