import React, { Component } from 'react';
import BenefitsDevOpsItem from './BenefitsDevOpsItem';

class BenefitsDevOps extends Component{
    render(){
        return(
            <section className="software_featured_area bg-light sec_pad">
                <div className="container">
                    <h2 className="sub_title text-center  wow fadeInUp" data-wow-delay="0.3s">Benefits of DevOps</h2>
                    <div className="row software_featured_info">
                        <BenefitsDevOpsItem icon="icon1.png" sTitle="Speed" 
                        Sp="Move at high velocity so you can innovate for customers faster, adapt to changing markets better, and grow more efficient at driving business results. The DevOps model enables your developers and operations teams to achieve these results."/>
                        <BenefitsDevOpsItem icon="icon2.png" sTitle="Rapid Delivery" 
                        Sp="Increase the frequency and pace of releases so you can innovate and improve your product faster. Continuous integration and continuous delivery are practices that automate the software release process, from build to deploy."/>
                        <BenefitsDevOpsItem icon="icon3.png" sTitle="Relibility" 
                        Sp="Ensure the quality of application updates and infrastructure changes so you can reliably deliver at a more rapid pace while maintaining a positive experience for end users."/>
                        <BenefitsDevOpsItem icon="icon1.png" sTitle="Scale" 
                        Sp="Operate and manage your infrastructure and development processes at scale. Automation and consistency help you manage complex or changing systems efficiently and with reduced risk."/>
                        <BenefitsDevOpsItem icon="icon2.png" sTitle="Improved Collaboration" 
                        Sp="Build more effective teams under a DevOps cultural model, which emphasizes values such as ownership and accountability. Developers and operations teams collaborate closely, share many responsibilities, and combine their workflows."/>
                        <BenefitsDevOpsItem icon="icon3.png" sTitle="Security" 
                        Sp="Move quickly while retaining control and preserving compliance. You can adopt a DevOps model without sacrificing security by using automated compliance policies, fine-grained controls, and configuration management techniques."/>
                    </div>
                </div>
            </section>
        )
    }
}
export default BenefitsDevOps;
