import React, {Component} from 'react';

class HowDevOpsWorks extends Component {
    render(){
        let {t} = this.props;
        return(
            <section className="devops_how_area sec_pad">
                <div className="container">
                    <div className="devops_how_info text-center ">
                        <h2 className="f_600 f_size_30 l_height30 t_color3 mb_50">How DevOps Works</h2>
                        <p className="pl-4 pr-4 f_size_20">Under a DevOps model, development and operations teams are no longer “siloed.” Sometimes, these two teams are merged into a single team where the engineers work across the entire application lifecycle, from development and test to deployment to operations, and develop a range of skills not limited to a single function.</p>
                        <p className="pl-4 pr-4 f_size_20">In some DevOps models, quality assurance and security teams may also become more tightly integrated with development and operations and throughout the application lifecycle. When security is the focus of everyone on a DevOps team, this is sometimes referred to as DevSecOps.</p>
                    </div>
                </div>
            </section>
         )
    }
}
export default HowDevOpsWorks;
