import './App.css';
import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { TranslatorProvider } from "react-translate";


/* ---- Pages ----*/
import Home from "./Pages/Home";
import DatabaseService from "./Pages/Database-service";
import TechnologyConsulting from "./Pages/Technology-consulting";
import SoftwareDevelopment from "./Pages/Software-development";
import WebAppServices from "./Pages/Web-app-services";
import About from "./Pages/About"
import Devops from "./Pages/Devops";
import Scrum from "./Pages/Scrum";
import Clients from "./Pages/Clients";

class App extends Component{

    constructor(props) {
        super(props);
        this.state = {language: 'en'};

        this.languageChange = this.languageChange.bind(this);

      }

      languageChange(event) {
        this.setState({language: event.target.value});
      }

   _getLayout(lang){
	   return (
	   <TranslatorProvider translations={require('./assets/translations/'+lang+'.json')}>	
            <Router>   
                {/* <a> 
                    <select defalutValue="" value={this.state.value} onChange={this.languageChange} >
                        <option value="es" >es</option>
                        <option selected={'selected'} value="en" >en</option>
                    </select>
                </a> */}
                <Switch>

                    <Route path="/database-service" component={DatabaseService} />
                    <Route path="/clients" component={Clients} />
                    <Route path="/technology-consulting" component={TechnologyConsulting} />
                    <Route path="/software-development" component={SoftwareDevelopment} />
                    <Route path="/web-App-Services" component={WebAppServices} />      
                    <Route path="/about" component={About} />     
                    <Route path="/devops" component={Devops} />
                    <Route path="/scrum" component={Scrum} />           
                    <Route exact={true} path={"/"} component={Home} />



                </Switch>
                <footer className="footer_area footer_area_four f_bg">
                        <div className="footer_bottom">
                            <div className="container">
                                <div className="row">
                                    <div className="col-lg-4 col-md-5 col-sm-6">
                                        <p className="mb-0 f_400">Â© DreamCode Soft 2018 All rights reserved.</p>
                                    </div>
                                    <div className="col-lg-4 col-md-3 col-sm-6">
                                        <div className="f_social_icon_two text-center">                                 
                                            <a href="https://wwww.facebook.com" key="1"><i className="ti-facebook"></i></a>
                                            <a href="https://wwww.facebook.com" key="2"><i className="ti-twitter-alt"></i></a>
                                            <a href="https://wwww.facebook.com" key="3"><i className="ti-linkedin"></i></a>
                                        </div>
                                    </div>
                                    <div className="col-lg-4 col-md-4 col-sm-12">
                                        <ul className="list-unstyled f_menu text-right">
                                            <li><a href=".#">Jobs</a></li>
                                            <li><a href=".#">Privacy Policy</a></li>
                                            <li><a> 
                                                <select defalutValue="" value={this.state.value} onChange={this.languageChange} >
                                                    <option value="es" >es</option>
                                                    <option selected={'selected'} value="en" >en</option>
                                                </select></a>
                                            </li>
                                            <li>
                                                <button className={'botton1'} onClick={()=>{ this.setState({language:'es'})}}>es</button>
                                                <button className={'botton1'} onClick={()=>{ this.setState({language:'en'})}}>en</button>
                                            </li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </div>
                </footer> 

            </Router>
	   </TranslatorProvider>)
   }

   render(){
	   return(
           this._getLayout(this.state.language)
           );

   };

    };

export default App;
