import React from 'react';

class DevOpsItem extends Component {
    render(){

        let { t } = this.props;

    return(
        <section className="devops_part_area sec_pad bg-light">
            <div className="container">
                <div className="d-flex">
                    <div className="devops_part_item">
                        <img src={require ('../../img/process/ticket-support.png').default} alt=""/>
                        <h4>Dev</h4>
                        <p className="f_size_20 f_500">The <strong>Developers</strong> focus their efforts on ‘pushing out code.’ Changes and enhancements to production systems and attendant failures in providing service are seen as part of this rush to get the latest and greatest code out to consumers.</p>
                    </div>
                    <div className="devops_part_item">
                        <img src={require ('../../img/process/ticket-support.png').default} alt=""/>
                        <h4>Ops</h4>
                        <p className="f_size_20 f_500">The <strong>Operations groups</strong>, on the other hand, are “reactive.” They are focused on service management and incident response. Teams supporting operations tend to use organization models closely aligned with ITSM and ITIL. </p>
                    </div>
                </div>
            </div>
        </section>
        )
    }
}

export default translate('DevOpsItem')(DevOpsItem);
