import React, { Component } from "react";
import EventFeaturesItem from './EventFeaturesItem';
import SeoTitle from '../Title/SeoTitle';


class EventFeatures extends Component {
    
    render(){
        return(
        <section className="event_features_area bg-white">
            <div className="container">
                <div className="hosting_title security_title text-center">
                  <SeoTitle Title="Team Scrum" TitleP="It consists of a Product owner, the Development team and a Scrum Master. It is self-organized and multifunctional. They deliver products iteratively and incrementally." />
                </div>
                <div className="row event_features_inner pt-5">
                    <EventFeaturesItem image="chat.png"  title="Product owner" 
                    ptext="In charge of maximizing value and managing the Product Backlog."
                    ptext2="He is the person who knows and understands what the users' needs are, knows the business from head to toe, and is responsible for transmitting that understanding to the development team (or solution team)." 
                    />
                    <EventFeaturesItem image="leader.png"  title="Development team" 
                    ptext="It is made up of developers, who executed the complex work beyond the code. They carry out project activities to create and deliver a preview of the finished product in each sprint." 
                    ptext2="They are self-organizing, to the extent that they decide themselves how to turn portfolio items into a true product increment throughout the sprint."
                    />
                    <EventFeaturesItem image="meetup.png"  title="Scrum Master" 
                    ptext="He is a leader who is at the service of the Scrum Team and the organization, helping them understand and live agility. He is responsible for promoting and supporting Scrum, its theory, practices, rules and values."
                    ptext2="He knows the status of the project, he is in charge of facilitating the events, he cares that the team is multifunctional and for its continuous improvement, it helps to remove obstacles that arise and that may prevent the achievement of the objectives."
                    /> 
                    
                </div>
            </div>
        </section>
        )
    }
}
export default EventFeatures;
